hash.slice(1) + ']');                      // Does a scroll target exist?                      if (target.length) {                        // Only prevent default if animation is actually gonna happen                        event.preventDefault();                        $('html, body').animate({                          scrollTop: target.offset().top                        }, 600, function() {                          // Callback after animation                          // Must change focus!                          var $target = $(target);                          $target.focus();                          if ($target.is(":focus")) { // Checking if the target was focused                            return false;                          } else {                            $target.attr('tabindex','-1'); // Adding tabindex for elements not focusable                            $target.focus(); // Set focus again                          };                        });                      }                    }                  });                // Update the current year in copyright                $('.tm-current-year').text(new Date().getFullYear());                      });    </script>       <script type="text/javascript">          $("#state").change(function(){        var id = $(this).val();        $.post("index.php", {get:"cities",id:id},function(data){            if(data ==""){                $("#district").append("<option value='1000000'>Other</option>");            }            else            {            $("#district").empty();            $("#district").prepend("<option value='' selected='selected'>--Select--</option>");            $("#district").append(data);            }        });    });        </script></body></html>